一、数据结构与算法：线性表1、顺序表实现代码： 123456789101112131415161718192021222324252627282930313233343536373839404142434445464748495051525354555657585960616263646566676869707172737475767778798081828384858687888990919293949596979899100101#include &lt;stdio.h&gt;#include &lt;stdlib.h&gt;typedef int E; //定义顺序表存储的数据为intstruct List{ E *array; //实现顺序表的底层数组 int capacity; //表示底层数组的容量 int size; //已存多少数据};//插入元素_Bool insertList(struct List *list, E element, int index){ if (index &lt; 1 || index &gt; list-&gt;size + 1) return 0; //index可能非法 if(list-&gt;size == list-&gt;capacity){ //判断顺序表是否满了，若满，则扩容。 int newCapacity = list-&gt;capacity + (list-&gt;capacity &gt;&gt; 1); //&gt;&gt;1 相当于/2 E * newList = realloc(list-&gt;array, newCapacity*sizeof(E)); if(newList == NULL) return 0; list-&gt;array = newList; list-&gt;capacity = newCapacity; } for (int i = list-&gt;size; i&gt;=index; --i){ list-&gt;array[i] = list-&gt;array[i-1]; } list-&gt;array[index - 1] = element; list-&gt;size++; return 1;}//删除元素_Bool deleteList(struct List *list,int index){ if(index &lt; 1 || index &gt; list-&gt;size) return 0; for(int i = index - 1; i &lt; list-&gt;size - 1; ++i){ list-&gt;array[i] = list-&gt;array[i+1]; } list-&gt;size--;}//活得顺序表大小int sizeList(struct List *list){ return list-&gt;size;}//获得元素E * getList(struct List *list, int index){ if(index &lt; 1 || index &gt; list-&gt;size) return NULL; return &amp;list-&gt;array[index-1];}//查找元素int findList(struct List *list,E element){ for(int i = 0; i &lt; list-&gt;size; i++){ if(list-&gt;array[i] == element) return i + 1; } return -1;}/******/_Bool initList(struct List *list ){ list-&gt;array = malloc(sizeof(E)*10); //malloc开辟的内存在堆区，函数生命周期结束后还存在（需要手动释放或程序结束后由系统释放）。 if (list-&gt;array == NULL) return 0; //若申请内存空间失败，则返回0. list-&gt;capacity = 10; list-&gt;size = 0; return 1;}void printList(struct List *list){ for(int i = 0; i&lt;list-&gt;size; ++i){ printf(&quot;%d &quot;, list-&gt;array[i]); } printf(&quot;
&quot;);}int main(int argc,int* argv){ struct List * list = malloc(sizeof(struct List *)); /*结构体指针初始化，首先不能 “= NULL”，因为指针指向一个安全的区域，不能解析。 **也不能，不初始化，因为指针可能指向未知地址，对其操作造成的后果是未知的， **初始化方式有：一、在堆区开辟一个空间；二、先定义一个结构体，用指针指向他； */ if(initList(list)){ for(int i = 0; i&lt;10; ++i){ insertList(list,i*10,i+1); } deleteList(list,2); printList(list); printf(&quot;%d 
&quot;,*getList(list,2)); printf(&quot;%d&quot;,findList(list,30)); } else{ printf(&quot;shibai&quot;); } free(list);} 顺序表是一种随机存取的存储结构。 2、链表实现代码： 123456789101112131415161718192021222324252627282930313233343536373839404142434445464748495051525354555657585960616263646566676869707172737475767778798081828384858687888990919293949596979899#include &lt;stdio.h&gt;#include &lt;stdlib.h&gt;typedef int E; //定义顺序表存储的数据为intstruct ListNode{ E element; struct ListNode *next;};typedef struct ListNode* Node;void initList(Node node){ node-&gt;next = NULL;}_Bool insertList(Node head, E element, int index){ if(index &lt; 0) return 0; while(--index){ head = head-&gt;next; if(head == NULL) return 0; } Node node = malloc(sizeof(struct ListNode)); if(node == NULL) return 0; node-&gt;element = element; node-&gt;next = head-&gt;next; head-&gt;next = node; return 1;}_Bool deleteList(Node head, int index){ if(index &lt; 1) return 0; while(--index){ head = head-&gt;next; if(head == NULL) return 0; } if(head-&gt;next == NULL) return 0; Node node = head-&gt;next; head-&gt;next = head-&gt;next-&gt;next; free(node); return 1;}//获得元素；E *getList(Node head, int index){ if(index &lt; 1) return 0; if (head-&gt;next == NULL) return NULL; //若为空表，则返回为空； do{ head = head-&gt;next; if(head == NULL) return NULL; }while(--index); return &amp;head-&gt;element;}//寻找元素下标int findList(Node head, E element){ int i = 1; if(head-&gt;next == NULL) return -1; //判断是否为空表 do{ head = head-&gt;next; if(head-&gt;element == element) return i; i++; }while(head-&gt;next); return -1;}int sizeList(Node head){ int i = 0; while(head-&gt;next){ i++; head = head-&gt;next; } return i;}//函数都是值传递，传值，值不变；传指针，指针不变；传指针，值会变//对head-&gt;element或者head-&gt;next操作会改变值void printfList(Node head){ while(head-&gt;next){ head = head-&gt;next; printf(&quot;%d &quot;,head-&gt;element); } printf(&quot;
&quot;);}int main(){ Node p1; struct ListNode head; initList(&amp;head); for(int i = 0; i &lt; 3; ++i){ insertList(&amp;head,i*10, i+1); } printfList(&amp;head); printf(&quot;%d&quot;,sizeList(&amp;head)); } 链表表是一种顺序访问的存储结构。 3、双向链表4、循环链表w5、堆栈（Stack）先进后出 6、队列（Queue）先进先出 实战1、反转链表题目描述：给你单链表的头节点 head ，请你反转链表，并返回反转后的链表。 解法一1234567891011121314151617181920212223#include &lt;stdio.h&gt;#include &lt;stdlib.h&gt;typedef int E; //定义顺序表存储的数据为intstruct ListNode{ int val; struct ListNode *next;};struct ListNode* reverseList(struct ListNode* head){ struct ListNode *tmp,*newHead = NULL; while (head) { //假设前面已被反转。 tmp = head-&gt;next; //保存第二个节点，用于当作下一个节点的头结点。 head-&gt;next = newHead; //指向前节点 newHead = head; //更新前节点 head = tmp; //新链表 } return newHead;} 解法二:递归1234567891011121314151617181920212223#include &lt;stdio.h&gt;#include &lt;stdlib.h&gt;typedef int E; //定义顺序表存储的数据为intstruct ListNode{ int val; struct ListNode *next;};struct ListNode* reverseList(struct ListNode* head) { if (head == NULL || head-&gt;next == NULL) { return head; } struct ListNode* newHead = reverseList(head-&gt;next); head-&gt;next-&gt;next = head; head-&gt;next = NULL; return newHead;} 实战2、匹配字符串给定一个只包括 '('，')'，'{'，'}'，'['，']' 的字符串 s ，判断字符串是否有效。 有效字符串需满足： 左括号必须用相同类型的右括号闭合。 左括号必须以正确的顺序闭合。 每个右括号都有一个对应的相同类型的左括号。 方法：栈的应用方法1：自己的12345678910111213141516171819202122232425262728293031323334353637383940414243444546474849505152535455565758596061626364656667686970717273747576777879808182#include&lt;stdio.h&gt;#include&lt;string.h&gt;#include&lt;stdlib.h&gt;&gt;#define true 1#define false 0typedef char E;struct LNode { E val; struct LNode *next;};typedef struct LNode* Node;void initStack(Node head){ head-&gt;next = NULL;}void printStack(Node head){ printf(&quot;| &quot;); head = head-&gt;next; while (head){ printf(&quot;%d &quot;, head-&gt;val); head = head-&gt;next; } printf(&quot;
&quot;);}//入栈_Bool pushStack(Node head, E val){ Node node = malloc(sizeof(struct LNode)); if(node == NULL) return 0; node-&gt;next = head-&gt;next; node-&gt;val = val; head-&gt;next = node; return 1;}//出栈E popStack(Node head){ if(head-&gt;next == NULL) return 0; Node node; node = head-&gt;next; E val = node-&gt;val; head-&gt;next = head-&gt;next-&gt;next; free(node); return val;} _Bool isValid(char* s) { struct LNode head; initStack(&amp;head); unsigned int len = strlen(s); if(len % 2 == 1) return false; pushStack(&amp;head,s[0]); for(int i = 1; i &lt; len; i++){ E top = popStack(&amp;head); if(top != 0)pushStack(&amp;head,top); if(top == '('){ if(s[i] == ')') popStack(&amp;head); else pushStack(&amp;head,s[i]); } else if(top == '['){ if(s[i] == ']') popStack(&amp;head); else pushStack(&amp;head,s[i]); } else if(top == '{'){ if(s[i] == '}') popStack(&amp;head); else pushStack(&amp;head,s[i]); } else pushStack(&amp;head,s[i]); } if(head.next == NULL) return true; else return false;}int main(){ char *s = &quot;()[]{}&quot;; printf(&quot;%d &quot;,isValid(s));} 方法2：更快123456789101112131415161718192021222324252627282930313233343536373839404142434445464748495051525354555657585960#include &lt;stdlib.h&gt;#include &lt;stdbool.h&gt;#include &lt;string.h&gt;typedef char E;struct LNode { E element; struct LNode * next;};typedef struct LNode * Node;void initStack(Node head){ head-&gt;next = NULL;}_Bool pushStack(Node head, E element){ Node node = malloc(sizeof(struct LNode)); if(node == NULL) return 0; node-&gt;next = head-&gt;next; node-&gt;element = element; head-&gt;next = node; return 1;}_Bool isEmpty(Node head){ return head-&gt;next == NULL;}E popStack(Node head){ Node top = head-&gt;next; head-&gt;next = head-&gt;next-&gt;next; E e = top-&gt;element; free(top); return e;}bool isValid(char * s){ unsigned long len = strlen(s); if(len % 2 == 1) return false; //如果长度不是偶数，那么一定不能成功匹配 struct LNode head; initStack(&amp;head); for (int i = 0; i &lt; len; ++i) { char c = s[i]; if(c == '(' || c == '[' || c == '{') { pushStack(&amp;head, c); }else { if(isEmpty(&amp;head)) return false; if(c == ')') { if(popStack(&amp;head) != '(') return false; } else if(c == ']') { if(popStack(&amp;head) != '[') return false; } else { if(popStack(&amp;head) != '{') return false; } } } return isEmpty(&amp;head);} 方法三：更快1234567891011121314151617181920212223242526char pairs(char a) { if (a == '}') return '{'; if (a == ']') return '['; if (a == ')') return '('; return 0;}bool isValid(char* s) { int n = strlen(s); if (n % 2 == 1) { return false; } int stk[n + 1], top = 0; for (int i = 0; i &lt; n; i++) { char ch = pairs(s[i]); if (ch) { if (top == 0 || stk[top - 1] != ch) { return false; } top--; } else { stk[top++] = s[i]; } } return top == 0;} 二、数据结构与算法：树1、树：理论 我们一般称位于最上方的结点为树的根结点（Root）； 每个结点连接的子结点数目（分支的数目），我们称为结点的度（Degree），而各个结点度的最大值称为树的度； 每个结点延伸下去的下一个结点都可以称为一棵子树（SubTree）； 每个结点的层次（Level）按照从上往下的顺序，树的根结点为1，每向下一层+1，比如G的层次就是3，整棵树中所有结点的最大层次，就是这颗树的深度（Depth）； 与当前结点直接向下相连的结点，我们称为子结点（Child）；相反即为父节点； 如果某个节点没有任何的子结点（结点度为0时）那么我们称这个结点为叶子结点； 如果两个结点的父结点是同一个，那么称这两个节点为兄弟结点（Sibling）； 从根结点开始一直到某个结点的整条路径的所有结点，都是这个结点的祖先结点（Ancestor）； 2、二叉树的性质 性质一： 对于一棵二叉树，第i层的最大结点数量为 个2^i - 1; 一棵深度为k的二叉树最大结点数量为 n = 2^k*−1，顺便得出，结点的边数为 E = n - 1。 于任何一棵二叉树，如果其叶子结点个数为 n0，度为2的结点个数为 n2 ，那么两者满足以下公式：n0 = n2+1; n个结点的完全二叉树深度为 k = log2^n + 1 ; 3、二叉树遍历：前序、中序、后序遍历 前序遍历结果为：ABDECF； 中序遍历结果为：DBEACF； 后序遍历结果为：DEBFCA； 12345678910111213141516171819202122232425262728293031323334353637383940414243444546474849505152535455565758596061626364#include&lt;stdio.h&gt;#include&lt;string.h&gt;#include&lt;stdlib.h&gt;&gt;typedef char E;struct TreeNode{ E element; struct TreeNode *left; struct TreeNode *right;};typedef struct TreeNode* Node;//前序递归，利用函数栈的特性，不断出栈入栈void preOrder(Node root){ if(root == NULL) return; printf(&quot;%c&quot;, root-&gt;element); preOrder(root-&gt;left); preOrder(root-&gt;right);}//中序遍历void inOrder(Node root){ if(root == NULL) return; inOrder(root-&gt;left); printf(&quot;%c&quot;,root-&gt;element); inOrder(root-&gt;right);}//后序遍历void afOrder(Node root){ if(root == NULL) return; afOrder(root-&gt;left); afOrder(root-&gt;right); printf(&quot;%c&quot;,root-&gt;element);}int main(){ Node a = malloc(sizeof(struct TreeNode)); Node b = malloc(sizeof(struct TreeNode)); Node c = malloc(sizeof(struct TreeNode)); Node d = malloc(sizeof(struct TreeNode)); Node e = malloc(sizeof(struct TreeNode)); Node f = malloc(sizeof(struct TreeNode)); a-&gt;element = 'A'; b-&gt;element = 'B'; c-&gt;element = 'C'; d-&gt;element = 'D'; e-&gt;element = 'E'; f-&gt;element = 'F'; a-&gt;left = b; a-&gt;right = c; b-&gt;left = d; b-&gt;right = e; c-&gt;right = f; c-&gt;left = NULL; d-&gt;left = e-&gt;right = NULL; e-&gt;left = e-&gt;right = NULL; f-&gt;left = f-&gt;right = NULL; afOrder(a);} 4、二叉树遍历：层序遍历12345678910111213141516171819202122232425262728293031323334353637383940414243444546474849505152535455565758596061626364656667686970717273747576777879808182838485868788899091929394#include&lt;stdio.h&gt;#include&lt;string.h&gt;#include&lt;stdlib.h&gt;typedef char E;typedef struct TreeNode { E element; //存放元素 struct TreeNode * left; //指向左子树的指针 struct TreeNode * right; //指向右子树的指针} *Node;typedef struct initNode{ //定义队列初始节点 Node element; struct initNode *next;} *INode;struct Queue{ //队列头尾指针 INode front,rear;};typedef struct Queue* LinkedQueue;_Bool initQueue(LinkedQueue queue){ INode node = malloc(sizeof(struct initNode)); if(node == NULL) return 0; node-&gt;next = NULL; queue-&gt;front = queue-&gt;rear = node;}//入队_Bool enQueue(LinkedQueue quene, Node element){ INode node = malloc(sizeof(struct initNode)); if(node == NULL) return 0; node-&gt;next = NULL; node-&gt;element = element; quene-&gt;rear-&gt;next = node; quene-&gt;rear = node; return 1;}// 出队Node deQueue(LinkedQueue queue){ Node val = queue-&gt;front-&gt;next-&gt;element; INode node; node = queue-&gt;front-&gt;next; queue-&gt;front-&gt;next = queue-&gt;front-&gt;next-&gt;next; if(queue-&gt;rear == node) queue-&gt;rear = queue-&gt;front; free(node); return val;}_Bool isEmpty(LinkedQueue queue){ return (queue-&gt;front == queue-&gt;rear);}void levelQueue(Node root){ struct Queue queue; initQueue(&amp;queue); enQueue(&amp;queue,root); while(!isEmpty(&amp;queue)){ Node node = deQueue(&amp;queue); printf(&quot;%c&quot;,node-&gt;element); if(node-&gt;left) enQueue(&amp;queue,node-&gt;left); if(node-&gt;right) enQueue(&amp;queue,node-&gt;right); }}int main(){ Node a = malloc(sizeof(struct TreeNode)); Node b = malloc(sizeof(struct TreeNode)); Node c = malloc(sizeof(struct TreeNode)); Node d = malloc(sizeof(struct TreeNode)); Node e = malloc(sizeof(struct TreeNode)); Node f = malloc(sizeof(struct TreeNode)); a-&gt;element = 'A'; b-&gt;element = 'B'; c-&gt;element = 'C'; d-&gt;element = 'D'; e-&gt;element = 'E'; f-&gt;element = 'F'; a-&gt;left = b; a-&gt;right = c; b-&gt;left = d; b-&gt;right = e; c-&gt;right = f; c-&gt;left = NULL; d-&gt;left = e-&gt;right = NULL; e-&gt;left = e-&gt;right = NULL; f-&gt;left = f-&gt;right = NULL; levelQueue(a);} 5、二叉树的线索化(以前序为例)12345678910111213141516171819202122232425262728293031323334353637383940414243444546474849505152535455565758596061626364656667#include&lt;stdio.h&gt;#include&lt;string.h&gt;#include&lt;stdlib.h&gt;typedef char E;typedef struct TreeNode { E element; //存放元素 struct TreeNode * left; //指向左子树的指针 struct TreeNode * right; //指向右子树的指针 char leftFlag,rightFlag //线索化标志位} *Node;Node pre = NULL; //这里我们需要一个pre来保存后续结点的指向void treeOrdered(Node root){ if(root == NULL) return; //线索化规则：结点的左指针，指向其当前遍历顺序的前驱结点；结点的右指针，指向其当前遍历顺序的后继结点。 if(root-&gt;left == NULL){ //判断当前节点的左节点是否为空，若为空，则指向上一个节点。 root-&gt;leftFlag = 1; root-&gt;left = pre; } if(pre &amp;&amp; pre-&gt;right == NULL){ //判断上一个节点的右节点是否为空，若为空，则指向当前节点 pre-&gt;right = root; pre-&gt;rightFlag = 1; } pre = root; if(root-&gt;leftFlag == 0) treeOrdered(root-&gt;left); //判断左节点是否是线索化的，若不是，才能继续。 if(root-&gt;rightFlag == 0) treeOrdered(root-&gt;right); //判断可略，因为，我们对右节点的线索化，是在后面执行的}void preOrder(Node root){ while (root) { //到头为止 printf(&quot;%c&quot;, root-&gt;element); //因为是前序遍历，所以直接按顺序打印就行了 if(root-&gt;leftFlag == 0) root = root-&gt;left; //如果是左孩子，那么就走左边 else root = root-&gt;right; //如果左边指向是线索，那么就直接走右边。 }}Node createNode(E element){ //单独写了个函数来创建结点 Node node = malloc(sizeof(struct TreeNode)); node-&gt;left = node-&gt;right = NULL; node-&gt;rightFlag = node-&gt;leftFlag = 0; node-&gt;element = element; return node;}int main() { Node a = createNode('A'); Node b = createNode('B'); Node c = createNode('C'); Node d = createNode('D'); Node e = createNode('E'); a-&gt;left = b; b-&gt;left = d; a-&gt;right = c; b-&gt;right = e; treeOrdered(a); preOrder(a);} 六、二叉搜索树（二叉查找树）七、平衡二叉树三、数据结构与算法：哈希表1、哈希表123456789101112131415161718192021222324252627282930313233343536373839404142434445464748495051525354#include&lt;stdio.h&gt;#include&lt;stdlib.h&gt;#define SIZE 9//结构体指针，直接用E-&gt;key访问，避免用*E访问typedef struct Element { //这里用一个Element将值包装一下 int key; //这里元素设定为int} * E;typedef struct HashTable{ //这里把数组封装为一个哈希表 E * table;} * HashTable;int hash(int key){ //哈希函数 return key % SIZE;}void init(HashTable hashTable){ //初始化函数 hashTable-&gt;table = malloc(sizeof(struct Element) * SIZE); for (int i = 0; i &lt; SIZE; ++i) hashTable-&gt;table[i] = NULL;}void insert(HashTable hashTable, E element){ //插入操作，为了方便就不考虑装满的情况了 int hashCode = hash(element-&gt;key); //首先计算元素的哈希值 hashTable-&gt;table[hashCode] = element; //对号入座}_Bool find(HashTable hashTable, int key){ int hashCode = hash(key); //首先计算元素的哈希值 if(!hashTable-&gt;table[hashCode]) return 0; //如果为NULL那就说明没有 return hashTable-&gt;table[hashCode]-&gt;key == key; //如果有，直接看是不是就完事}E create(int key){ //创建一个新的元素 E e = malloc(sizeof(struct Element)); e-&gt;key = key; return e;}int main() { struct HashTable hashTable; init(&amp;hashTable); insert(&amp;hashTable, create(10)); insert(&amp;hashTable, create(7)); insert(&amp;hashTable, create(13)); insert(&amp;hashTable, create(29)); printf(&quot;%d
&quot;, find(&amp;hashTable, 1)); printf(&quot;%d
&quot;, find(&amp;hashTable, 13));} 2、哈希冲突（链地址法）12345678910111213141516171819202122232425262728293031323334353637383940414243444546474849505152535455565758596061#include&lt;stdio.h&gt;#include&lt;stdlib.h&gt;#define SIZE 9typedef struct ListNode { //结点定义 int key; struct ListNode * next;} * Node;typedef struct HashTable{ //哈希表 struct ListNode * table; //这个数组专门保存头结点} * HashTable;void init(HashTable hashTable){ hashTable-&gt;table = malloc(sizeof(struct ListNode) * SIZE); for (int i = 0; i &lt; SIZE; ++i) { hashTable-&gt;table[i].key = -1; //将头结点key置为-1，next指向NULL hashTable-&gt;table[i].next = NULL; }}int hash(int key){ //哈希函数 return key % SIZE;}Node createNode(int key){ //创建结点专用函数 Node node = malloc(sizeof(struct ListNode)); node-&gt;key = key; node-&gt;next = NULL; return node;}void insert(HashTable hashTable, int key){ int hashCode = hash(key); Node head = hashTable-&gt;table + hashCode; //先计算哈希值，找到位置后直接往链表后面插入结点就完事了 while (head-&gt;next) head = head-&gt;next; head-&gt;next = createNode(key); //插入新的结点}_Bool find(HashTable hashTable, int key){ int hashCode = hash(key); Node head = hashTable-&gt;table + hashCode; while (head-&gt;next &amp;&amp; head-&gt;key != key) //直到最后或是找到为止 head = head-&gt;next; return head-&gt;key == key; //直接返回是否找到}int main(){ struct HashTable table; init(&amp;table); insert(&amp;table, 10); insert(&amp;table, 19); insert(&amp;table, 20); printf(&quot;%d
&quot;, find(&amp;table, 20)); printf(&quot;%d
&quot;, find(&amp;table, 17)); printf(&quot;%d
&quot;, find(&amp;table, 19));}

将原始的坐标数据，转化成你希望的坐标系，python程序如下：  tkinter as tkfrom tkinter import ttk, messageboximport numpy as npimport sv_ttk # 添加在import部分class CoordinateTransformer: def __init__(self, root): self.root = root self.root.title(&quot;坐标转换工具-LSK&quot;) # 设置全局默认字体（黑体 10号） self.root.option_add(&quot;*Font&quot;, (&quot;黑体&quot;, 10)) sv_ttk.set_theme(&quot;light&quot;) # 设置默认light主题 self.setup_ui() # 初始化数据 self.points = [] self.transformed_points = [] self.y_direction = 1 # 加载示例数据 self.load_sample_data() def setup_ui(self): &quot;&quot;&quot;初始化现代化用户界面&quot;&quot;&quot; self.root.geometry(&quot;900x600&quot;) main_frame = ttk.Frame(self.root, padding=10) main_frame.pack(fill=&quot;both&quot;, expand=True) # 左侧控制面板 control_frame = ttk.Frame(main_frame, width=250, padding=10) control_frame.pack(side=&quot;left&quot;, fill=&quot;y&quot;) # 右侧绘图区 plot_frame = ttk.Frame(main_frame, padding=10) plot_frame.pack(side=&quot;right&quot;, fill=&quot;both&quot;, expand=True) # 添加绘图区标题 plot_title = ttk.Label(plot_frame, text=&quot;坐标点分布图&quot;, font=(&quot;黑体&quot;, 10, &quot;bold&quot;)) plot_title.pack(side=&quot;top&quot;, pady=(0, 5)) # 画布容器保持不变 self.canvas = tk.Canvas(plot_frame, bg=&quot;white&quot;, bd=0, highlightthickness=0) self.canvas.pack(fill=&quot;both&quot;, expand=True) # 坐标输入区 input_frame = ttk.LabelFrame(control_frame, text=&quot;原始坐标&quot;, padding=10) input_frame.pack(fill=&quot;x&quot;, pady=5) self.coord_text = tk.Text(input_frame, height=10, width=30, font=(&quot;Consolas&quot;, 9)) self.coord_text.pack(fill=&quot;x&quot;) # 参考点设置 ref_frame = ttk.LabelFrame(control_frame, text=&quot;参考点设置&quot;, padding=10) ref_frame.pack(fill=&quot;x&quot;, pady=5) # 原点坐标(a) ttk.Label(ref_frame, text=&quot;原点(a) x,y:&quot;).grid(row=0, column=0, sticky=&quot;w&quot;) self.a_x = ttk.Entry(ref_frame, width=10, font=(&quot;Consolas&quot;, 10)) self.a_x.grid(row=0, column=1, padx=5, pady=2) self.a_y = ttk.Entry(ref_frame, width=10, font=(&quot;Consolas&quot;, 10)) self.a_y.grid(row=0, column=2, padx=5, pady=2) # x轴点坐标(b) ttk.Label(ref_frame, text=&quot;X 轴(b) x,y:&quot;).grid(row=1, column=0, sticky=&quot;w&quot;) self.b_x = ttk.Entry(ref_frame, width=10, font=(&quot;Consolas&quot;, 10)) self.b_x.grid(row=1, column=1, padx=5, pady=2) self.b_y = ttk.Entry(ref_frame, width=10, font=(&quot;Consolas&quot;, 10)) self.b_y.grid(row=1, column=2, padx=5, pady=2) # Y轴方向 self.y_dir_var = tk.IntVar(value=1) ttk.Checkbutton(ref_frame, text=&quot;反转Y轴方向&quot;, variable=self.y_dir_var).grid(row=2, column=0, columnspan=3, pady=5, sticky=&quot;w&quot;) # 操作按钮 btn_frame = ttk.Frame(control_frame) btn_frame.pack(fill=&quot;x&quot;, pady=10) #ttk.Button(btn_frame, text=&quot;转换坐标&quot;, style=&quot;Accent.TButton&quot;, command=self.transform_coords).pack(side=&quot;left&quot;, padx=2) ttk.Button(btn_frame, text=&quot;转换坐标&quot;, command=self.transform_coords).pack(side=&quot;left&quot;, padx=2) ttk.Button(btn_frame, text=&quot;复制结果&quot;, command=self.copy_results).pack(side=&quot;left&quot;, padx=2) ttk.Button(btn_frame, text=&quot;清空&quot;, command=self.clear_all).pack(side=&quot;right&quot;, padx=2) ttk.Button(btn_frame, text=&quot;示例数据&quot;, command=self.load_sample_data).pack(side=&quot;right&quot;, padx=2) # 结果显示 result_frame = ttk.LabelFrame(control_frame, text=&quot;转换结果&quot;, padding=10) result_frame.pack(fill=&quot;x&quot;, pady=5) self.result_text = tk.Text(result_frame, height=10, width=30, state=&quot;disabled&quot;, font=(&quot;Consolas&quot;, 9)) self.result_text.pack(fill=&quot;x&quot;) # 绑定事件 self.canvas.bind(&quot;&lt;Configure&gt;&quot;, self.draw_coordinates) def load_sample_data(self): &quot;&quot;&quot;加载示例数据&quot;&quot;&quot; sample_data = &quot;&quot;&quot;-3.079,-40.615 -14.124,-38.0110.771,-17.989-10.172,-16.0685.609,10.806-5.774,10.9199.346,34.571-1.755,36.34713.532,58.9042.343,61.267 &quot;&quot;&quot; self.coord_text.delete(&quot;1.0&quot;, tk.END) self.coord_text.insert(&quot;1.0&quot;, sample_data.strip()) self.y_dir_var.set(True) # 设置示例参考点坐标 self.a_x.delete(0, tk.END) self.a_x.insert(0, &quot;-3.079&quot;) self.a_y.delete(0, tk.END) self.a_y.insert(0, &quot;-40.615&quot;) self.b_x.delete(0, tk.END) self.b_x.insert(0, &quot;0.771&quot;) self.b_y.delete(0, tk.END) self.b_y.insert(0, &quot;-17.989&quot;) def parse_input(self): &quot;&quot;&quot;解析输入的坐标数据&quot;&quot;&quot; self.points = [] text = self.coord_text.get(&quot;1.0&quot;, tk.END).strip() for line in text.split(&quot;
&quot;): if line.strip(): try: x, y = map(float, line.strip().split(&quot;,&quot;)) self.points.append([x, y]) except ValueError: continue return len(self.points) &gt; 0 def transform_coords(self): &quot;&quot;&quot;执行坐标转换&quot;&quot;&quot; if not self.parse_input(): messagebox.showerror(&quot;错误&quot;, &quot;请输入有效的坐标数据&quot;) return try: a_x = float(self.a_x.get()) a_y = float(self.a_y.get()) b_x = float(self.b_x.get()) b_y = float(self.b_y.get()) self.y_direction = -1 if self.y_dir_var.get() else 1 except ValueError: messagebox.showerror(&quot;错误&quot;, &quot;请设置有效的参考点坐标&quot;) return a = np.array([a_x, a_y]) b = np.array([b_x, b_y]) ab = b - a # 计算新坐标系基向量 e1 = ab / np.linalg.norm(ab) # X轴 e2 = np.array([-e1[1], e1[0]]) * (-1 if self.y_dir_var.get() else 1) # 保持功能但写法更清晰 # 构造变换矩阵 transform_matrix = np.array([e1, e2]).T # 转换所有点 self.transformed_points = [] for point in self.points: translated = np.array(point) - a new_coords = np.dot(transform_matrix.T, translated) self.transformed_points.append(new_coords.tolist()) # 显示结果 self.show_results() self.draw_coordinates() def show_results(self): &quot;&quot;&quot;显示转换结果(保留8位小数)&quot;&quot;&quot; self.result_text.config(state=&quot;normal&quot;) self.result_text.delete(&quot;1.0&quot;, tk.END) for i, point in enumerate(self.transformed_points): self.result_text.insert(tk.END, f&quot;{i:2d}: {point[0]:.8f}, {point[1]:.8f}
&quot;) self.result_text.config(state=&quot;disabled&quot;) def copy_results(self): &quot;&quot;&quot;复制结果到剪贴板(保留8位小数)&quot;&quot;&quot; if not self.transformed_points: return self.root.clipboard_clear() result = &quot;
&quot;.join([f&quot;{p[0]:.8f}, {p[1]:.8f}&quot; for p in self.transformed_points]) self.root.clipboard_append(result) messagebox.showinfo(&quot;成功&quot;, &quot;转换结果已复制到剪贴板&quot;) def clear_all(self): &quot;&quot;&quot;清空所有数据&quot;&quot;&quot; self.coord_text.delete(&quot;1.0&quot;, tk.END) self.result_text.config(state=&quot;normal&quot;) self.result_text.delete(&quot;1.0&quot;, tk.END) self.result_text.config(state=&quot;disabled&quot;) self.points = [] self.transformed_points = [] self.a_x.delete(0, tk.END) self.a_y.delete(0, tk.END) self.b_x.delete(0, tk.END) self.b_y.delete(0, tk.END) self.y_dir_var.set(1) self.canvas.delete(&quot;all&quot;) def draw_coordinates(self, event=None): &quot;&quot;&quot;绘制坐标点&quot;&quot;&quot; if not self.points: return self.canvas.delete(&quot;all&quot;) width = self.canvas.winfo_width() height = self.canvas.winfo_height() # 计算缩放和平移参数 all_points = self.points + ([p[:2] for p in self.transformed_points] if self.transformed_points else []) x_coords = [p[0] for p in all_points] y_coords = [p[1] for p in all_points] # 包含参考点 try: a_x = float(self.a_x.get()) a_y = float(self.a_y.get()) x_coords.append(a_x) y_coords.append(a_y) except ValueError: pass try: b_x = float(self.b_x.get()) b_y = float(self.b_y.get()) x_coords.append(b_x) y_coords.append(b_y) except ValueError: pass x_min, x_max = min(x_coords), max(x_coords) y_min, y_max = min(y_coords), max(y_coords) # 添加2%的边距 x_margin = (x_max - x_min) * 0.02 y_margin = (y_max - y_min) * 0.02 x_min -= x_margin if x_margin != 0 else 1 x_max += x_margin if x_margin != 0 else 1 y_min -= y_margin if y_margin != 0 else 1 y_max += y_margin if y_margin != 0 else 1 x_scale = width / (x_max - x_min) y_scale = height / (y_max - y_min) scale = min(x_scale, y_scale) * 0.98 # 保持纵横比 # 转换函数 def transform_point(x, y): canvas_x = (x - x_min) * scale + (width - (x_max - x_min) * scale) / 2 canvas_y = height - ((y - y_min) * scale + (height - (y_max - y_min) * scale) / 2) return canvas_x, canvas_y # 绘制坐标轴 ox, oy = transform_point(0, 0) # 原点坐标 self.canvas.create_line(0, oy, width, oy, fill=&quot;#999999&quot;, dash=(2,2), arrow=tk.LAST) # X轴 self.canvas.create_line(ox, height, ox, 0, fill=&quot;#999999&quot;, dash=(2,2), arrow=tk.LAST) # Y轴 # 绘制原始坐标点（红色） for i, point in enumerate(self.points): x, y = transform_point(point[0], point[1]) color = &quot;#FF4444&quot; # 红色 self.canvas.create_oval(x-5, y-5, x+5, y+5, fill=color, outline=&quot;black&quot;, width=1) self.canvas.create_text(x, y+15, text=str(i), font=(&quot;Arial&quot;, 8)) # 绘制转换后坐标点（绿色） if self.transformed_points: for i, point in enumerate(self.transformed_points): x, y = transform_point(point[0], point[1]) color = &quot;#44CC44&quot; # 绿色 self.canvas.create_oval(x-5, y-5, x+5, y+5, fill=color, outline=&quot;black&quot;, width=1) self.canvas.create_text(x, y-15, text=str(i), font=(&quot;Arial&quot;, 8))if __name__ == &quot;__main__&quot;: root = tk.Tk() app = CoordinateTransformer(root) root.mainloop()# pyinstaller -F -w -i my.ico --hidden-import numpy name.py
